func bigFromStr(s string) *big.Int {
	x, _ := new(big.Int).SetString(s, 10)
	return x
}

func ecPointFromStr(x, y string) *crypto.ECPoint {
	return crypto.NewECPointNoCurveCheck(btcec.S256(), bigFromStr(x), bigFromStr(y))
}

